import api from './api';

// Registrar ingreso de stock
export const registrarIngresoStock = (data) => api.post('/api/v1/inventario/ingreso', data);

// Registrar egreso de stock
export const registrarEgresoStock = (data) => api.post('/api/v1/inventario/egreso', data);

// Registrar ajuste de stock (opcional)
export const registrarAjusteStock = (data) => api.post('/api/v1/inventario/ajuste', data);

// Obtener historial de movimientos de un producto
export const obtenerMovimientosProducto = (productoId) => api.get(`/api/v1/inventario/movimientos/${productoId}`);
