// ==========================================
// ARCHIVO: frontend/src/componentes/productos/TarjetaProducto.jsx
// ==========================================
import React from 'react';
import { motion } from 'framer-motion';
import { 
  ShoppingCartIcon, 
  HeartIcon,
  StarIcon,
  EyeIcon
} from '@heroicons/react/24/outline';
import { HeartIcon as HeartSolid, StarIcon as StarSolid } from '@heroicons/react/24/solid';
import { Link } from 'react-router-dom';

const TarjetaProducto = ({ 
  producto, 
  onAgregarAlCarrito, 
  onToggleFavorito, 
  esFavorito = false,
  mostrarAcciones = true 
}) => {
  const formatearPrecio = (precio) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP'
    }).format(precio);
  };

  const obtenerImagenProducto = (imagen) => {
    if (!imagen) {
      return '/assets/imagenes/productos/placeholder.jpg';
    }
    
    // Si la imagen ya tiene una URL completa, la devolvemos
    if (imagen.startsWith('http')) {
      return imagen;
    }
    
    // Si es solo el nombre del archivo, construimos la ruta
    return `/assets/imagenes/productos/${imagen}`;
  };

  const tieneDescuento = producto.precio_oferta && producto.precio_oferta < producto.precio;

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      className="bg-white rounded-lg shadow-md hover:shadow-lg transition-all duration-300 overflow-hidden group"
    >
      {/* Imagen del producto */}
      <div className="relative aspect-square overflow-hidden bg-gray-100">
        <img
          src={obtenerImagenProducto(producto.imagen)}
          alt={producto.nombre}
          className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
          onError={(e) => {
            e.target.src = '/assets/imagenes/productos/placeholder.jpg';
          }}
        />
        
        {/* Badge de descuento */}
        {tieneDescuento && (
          <div className="absolute top-2 left-2 bg-red-500 text-white text-xs font-bold px-2 py-1 rounded">
            -{Math.round(((producto.precio - producto.precio_oferta) / producto.precio) * 100)}%
          </div>
        )}

        {/* Badge de marca */}
        {producto.marca && (
          <div className="absolute top-2 right-2 bg-blue-500 text-white text-xs font-bold px-2 py-1 rounded">
            {producto.marca.nombre}
          </div>
        )}

        {/* Acciones rápidas */}
        {mostrarAcciones && (
          <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-20 transition-all duration-300 flex items-center justify-center">
            <div className="flex space-x-2 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
              <button
                onClick={() => onToggleFavorito(producto.id)}
                className="bg-white p-2 rounded-full shadow-lg hover:bg-red-50 transition-colors"
              >
                {esFavorito ? (
                  <HeartSolid className="w-5 h-5 text-red-500" />
                ) : (
                  <HeartIcon className="w-5 h-5 text-gray-600" />
                )}
              </button>
              
              <Link
                to={`/producto/${producto.id}`}
                className="bg-white p-2 rounded-full shadow-lg hover:bg-blue-50 transition-colors"
              >
                <EyeIcon className="w-5 h-5 text-gray-600" />
              </Link>
            </div>
          </div>
        )}
      </div>

      {/* Información del producto */}
      <div className="p-4">
        {/* Categoría */}
        {producto.categoria && (
          <div className="text-xs text-gray-500 mb-1">
            {producto.categoria.nombre}
          </div>
        )}

        {/* Nombre del producto */}
        <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2 group-hover:text-blue-600 transition-colors">
          {producto.nombre}
        </h3>

        {/* Descripción */}
        {producto.descripcion && (
          <p className="text-sm text-gray-600 mb-3 line-clamp-2">
            {producto.descripcion}
          </p>
        )}

        {/* Precios */}
        <div className="mb-3">
          {tieneDescuento ? (
            <div className="flex items-center space-x-2">
              <span className="text-lg font-bold text-red-600">
                {formatearPrecio(producto.precio_oferta)}
              </span>
              <span className="text-sm text-gray-500 line-through">
                {formatearPrecio(producto.precio)}
              </span>
            </div>
          ) : (
            <span className="text-lg font-bold text-gray-900">
              {formatearPrecio(producto.precio)}
            </span>
          )}
        </div>

        {/* Stock */}
        <div className="text-sm text-gray-600 mb-3">
          {producto.stock > 0 ? (
            <span className="text-green-600">✓ En stock</span>
          ) : (
            <span className="text-red-600">✗ Sin stock</span>
          )}
        </div>

        {/* Botón de agregar al carrito */}
        {mostrarAcciones && producto.stock > 0 && (
          <button
            onClick={() => onAgregarAlCarrito(producto)}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-lg flex items-center justify-center space-x-2 transition-colors"
          >
            <ShoppingCartIcon className="w-5 h-5" />
            <span>Agregar al carrito</span>
          </button>
        )}

        {/* Botón de ver detalles si no hay stock */}
        {mostrarAcciones && producto.stock <= 0 && (
          <Link
            to={`/producto/${producto.id}`}
            className="w-full bg-gray-600 hover:bg-gray-700 text-white py-2 px-4 rounded-lg flex items-center justify-center space-x-2 transition-colors"
          >
            <EyeIcon className="w-5 h-5" />
            <span>Ver detalles</span>
          </Link>
        )}
      </div>
    </motion.div>
  );
};

export default TarjetaProducto;
