// ==========================================
// ARCHIVO: frontend/src/componentes/comun/CarritoCompras.jsx
// ==========================================
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  XMarkIcon, 
  ShoppingBagIcon, 
  PlusIcon, 
  MinusIcon,
  TrashIcon,
  CreditCardIcon
} from '@heroicons/react/24/outline';
import { useCarrito } from '../../contexto/ContextoCarrito';
import RealizarPedido from './RealizarPedido';

const CarritoCompras = ({ abierto, onCerrar }) => {
  const { 
    carrito, 
    eliminarDelCarrito, 
    actualizarCantidad, 
    limpiarCarrito, 
    obtenerTotal,
    obtenerCantidadTotal 
  } = useCarrito();

  const [pedidoAbierto, setPedidoAbierto] = useState(false);

  const formatearPrecio = (precio) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP'
    }).format(precio);
  };

  const total = obtenerTotal();
  const cantidadTotal = obtenerCantidadTotal();

  const handleRealizarPedido = () => {
    setPedidoAbierto(true);
  };

  return (
    <>
      <AnimatePresence>
        {abierto && (
          <>
            {/* Overlay */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={onCerrar}
              className="fixed inset-0 bg-black bg-opacity-50 z-40"
            />

            {/* Panel del carrito */}
            <motion.div
              initial={{ x: '100%' }}
              animate={{ x: 0 }}
              exit={{ x: '100%' }}
              transition={{ type: 'spring', damping: 25, stiffness: 200 }}
              className="fixed right-0 top-0 h-full w-full max-w-md bg-white shadow-xl z-50 flex flex-col"
            >
              {/* Header */}
              <div className="flex items-center justify-between p-4 border-b bg-blue-600 text-white">
                <div className="flex items-center">
                  <ShoppingBagIcon className="h-6 w-6 mr-2" />
                  <h2 className="text-lg font-semibold">
                    Mi Carrito ({cantidadTotal})
                  </h2>
                </div>
                <button
                  onClick={onCerrar}
                  className="p-2 hover:bg-blue-700 rounded-full transition-colors"
                >
                  <XMarkIcon className="h-6 w-6" />
                </button>
              </div>

              {/* Contenido del carrito */}
              <div className="flex-1 overflow-y-auto">
                {carrito.length === 0 ? (
                  <div className="flex flex-col items-center justify-center h-full text-gray-500 p-8">
                    <ShoppingBagIcon className="h-16 w-16 mb-4" />
                    <h3 className="text-lg font-medium mb-2">Tu carrito está vacío</h3>
                    <p className="text-sm text-center">
                      Agrega algunos productos para comenzar tu compra
                    </p>
                  </div>
                ) : (
                  <div className="p-4 space-y-4">
                    {/* Items del carrito */}
                    {carrito.map((item) => (
                      <motion.div
                        key={item.id}
                        layout
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -20 }}
                        className="bg-gray-50 rounded-lg p-4"
                      >
                        <div className="flex gap-3">
                          {/* Imagen del producto */}
                          <img
                            src={item.imagen}
                            alt={item.nombre}
                            className="w-16 h-16 object-cover rounded-md flex-shrink-0"
                            onError={(e) => {
                              e.target.src = '/images/placeholder-product.jpg';
                            }}
                          />

                          {/* Información del producto */}
                          <div className="flex-1 min-w-0">
                            <h4 className="font-medium text-gray-900 truncate">
                              {item.nombre}
                            </h4>
                            <p className="text-sm text-gray-600">
                              {item.marca?.nombre || 'Sin marca'}
                            </p>
                            <p className="text-sm font-medium text-blue-600">
                              {formatearPrecio(item.precio)}
                            </p>
                          </div>

                          {/* Botón eliminar */}
                          <button
                            onClick={() => eliminarDelCarrito(item.id)}
                            className="p-1 hover:bg-red-100 rounded text-red-500"
                            title="Eliminar producto"
                          >
                            <TrashIcon className="h-4 w-4" />
                          </button>
                        </div>

                        {/* Controles de cantidad */}
                        <div className="flex items-center justify-between mt-3">
                          <div className="flex items-center border rounded-md">
                            <button
                              onClick={() => actualizarCantidad(item.id, item.cantidad - 1)}
                              disabled={item.cantidad <= 1}
                              className="p-1 hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                              <MinusIcon className="h-4 w-4" />
                            </button>
                            <span className="px-3 py-1 text-sm font-medium">
                              {item.cantidad}
                            </span>
                            <button
                              onClick={() => actualizarCantidad(item.id, item.cantidad + 1)}
                              className="p-1 hover:bg-gray-100"
                            >
                              <PlusIcon className="h-4 w-4" />
                            </button>
                          </div>

                          <div className="text-sm font-medium">
                            {formatearPrecio(item.precio * item.cantidad)}
                          </div>
                        </div>
                      </motion.div>
                    ))}
                  </div>
                )}
              </div>

              {/* Footer con totales y botón de pago */}
              {carrito.length > 0 && (
                <div className="border-t p-4 space-y-4">
                  {/* Resumen de totales */}
                  <div className="space-y-2">
                    <div className="flex justify-between text-lg font-bold border-t pt-2">
                      <span>Total</span>
                      <span className="text-blue-600">{formatearPrecio(total)}</span>
                    </div>
                  </div>

                  {/* Botones de acción */}
                  <div className="space-y-2">
                    <button
                      onClick={handleRealizarPedido}
                      className="w-full bg-blue-600 text-white py-3 rounded-lg font-medium hover:bg-blue-700 transition-colors flex items-center justify-center"
                    >
                      <CreditCardIcon className="h-5 w-5 mr-2" />
                      Realizar Pedido
                    </button>
                    <button
                      onClick={limpiarCarrito}
                      className="w-full border border-gray-300 text-gray-700 py-2 rounded-lg font-medium hover:bg-gray-50 transition-colors"
                    >
                      Vaciar Carrito
                    </button>
                  </div>

                  {/* Información adicional */}
                  <div className="text-xs text-gray-600 text-center space-y-1">
                    <p className="flex items-center justify-center">
                      <span className="mr-1">🔒</span>
                      Compra segura con encriptación SSL
                    </p>
                    <p className="flex items-center justify-center">
                      <span className="mr-1">📞</span>
                      ¿Necesitas ayuda? Llama al (56) 2 2XXX XXXX
                    </p>
                    <p className="flex items-center justify-center">
                      <span className="mr-1">🚚</span>
                      Envío gratis en retiro en tienda
                    </p>
                  </div>
                </div>
              )}
            </motion.div>
          </>
        )}
      </AnimatePresence>

      {/* Componente Realizar Pedido */}
      <RealizarPedido 
        abierto={pedidoAbierto} 
        onCerrar={() => setPedidoAbierto(false)} 
      />
    </>
  );
};

export default CarritoCompras;
