import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useForm } from 'react-hook-form';
import { EyeIcon, EyeSlashIcon } from '@heroicons/react/24/outline';
import { useAuth } from '../../hooks/useAuth';
import Cargando from '../comun/Cargando';

const IniciarSesion = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { iniciarSesion, cargando } = useAuth();
  const [mostrarPassword, setMostrarPassword] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    defaultValues: {
      email: 'admin@ferremas.cl',
      password: 'password123',
    },
  });

  const onSubmit = async (data) => {
    const resultado = await iniciarSesion(data);
    if (resultado.exito) {
      const destino = location.state?.from?.pathname || '/tablero';
      navigate(destino, { replace: true });
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="w-full max-w-md mx-auto"
    >
      {/* Logo y Título */}
      <div className="text-center mb-8">
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ delay: 0.2, type: 'spring', stiffness: 200 }}
          className="w-16 h-16 bg-gradient-to-br from-blue-500 to-blue-600 rounded-2xl flex items-center justify-center mx-auto mb-4"
        >
          <span className="text-white font-bold text-2xl">F</span>
        </motion.div>
        <h2 className="text-3xl font-bold text-gray-900 dark:text-white">
          Bienvenido a FERREMAS
        </h2>
        <p className="text-gray-600 dark:text-gray-400 mt-2">
          Inicia sesión en tu cuenta
        </p>
      </div>

      {/* Formulario */}
      <motion.form
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3 }}
        onSubmit={handleSubmit(onSubmit)}
        className="space-y-6"
      >
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-8 border border-gray-200 dark:border-gray-700">
          {/* Campo Email */}
          <div className="space-y-1">
            <label className="form-label">
              Correo Electrónico
            </label>
            <input
              {...register('email', {
                required: 'El email es requerido',
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: 'Email inválido',
                },
              })}
              type="email"
              className="form-input"
              placeholder="admin@ferremas.cl"
            />
            {errors.email && (
              <p className="text-red-600 text-xs mt-1">{errors.email.message}</p>
            )}
          </div>

          {/* Campo Password */}
          <div className="space-y-1">
            <label className="form-label">
              Contraseña
            </label>
            <div className="relative">
              <input
                {...register('password', {
                  required: 'La contraseña es requerida',
                  minLength: {
                    value: 6,
                    message: 'La contraseña debe tener al menos 6 caracteres',
                  },
                })}
                type={mostrarPassword ? 'text' : 'password'}
                className="form-input pr-10"
                placeholder="••••••••"
              />
              <button
                type="button"
                onClick={() => setMostrarPassword(!mostrarPassword)}
                className="absolute inset-y-0 right-0 pr-3 flex items-center"
              >
                {mostrarPassword ? (
                  <EyeSlashIcon className="w-5 h-5 text-gray-400" />
                ) : (
                  <EyeIcon className="w-5 h-5 text-gray-400" />
                )}
              </button>
            </div>
            {errors.password && (
              <p className="text-red-600 text-xs mt-1">{errors.password.message}</p>
            )}
          </div>

          {/* Recordar sesión */}
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="recordar"
                type="checkbox"
                className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
              />
              <label htmlFor="recordar" className="ml-2 text-sm text-gray-600 dark:text-gray-400">
                Recordar sesión
              </label>
            </div>
            <Link
              to="/recuperar-password"
              className="text-sm text-blue-600 hover:text-blue-500 dark:text-blue-400"
            >
              ¿Olvidaste tu contraseña?
            </Link>
          </div>

          {/* Botón de Login */}
          <button
            type="submit"
            disabled={cargando}
            className="w-full btn btn-primary py-3 text-base font-semibold"
          >
            {cargando ? (
              <Cargando tamaño="sm" mensaje="Iniciando sesión..." centrado={false} />
            ) : (
              'Iniciar Sesión'
            )}
          </button>
        </div>
      </motion.form>

      {/* Credenciales de prueba */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5 }}
        className="mt-6 p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-700"
      >
        <h4 className="text-sm font-medium text-blue-900 dark:text-blue-200 mb-2">
          Credenciales de Prueba:
        </h4>
        <div className="text-xs text-blue-700 dark:text-blue-300 space-y-1">
          <p><strong>Admin:</strong> admin@ferremas.cl / password123</p>
          <p><strong>Cliente:</strong> cliente@test.com / password123</p>
        </div>
      </motion.div>

      {/* Enlace a registro */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.6 }}
        className="text-center mt-6"
      >
        <p className="text-gray-600 dark:text-gray-400">
          ¿No tienes una cuenta?{' '}
          <Link
            to="/registrarse"
            className="text-blue-600 hover:text-blue-500 dark:text-blue-400 font-medium"
          >
            Regístrate aquí
          </Link>
        </p>
      </motion.div>
    </motion.div>
  );
};

export default IniciarSesion;
