import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
    Box,
    Card,
    CardContent,
    Typography,
    CircularProgress,
    Button,
    Alert
} from '@mui/material';
import api from '../servicios/api';
import { useSnackbar } from 'notistack';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';

const ConfirmacionPago = () => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [resultado, setResultado] = useState(null);
    const navigate = useNavigate();
    const location = useLocation();
    const { enqueueSnackbar } = useSnackbar();

    useEffect(() => {
        const confirmarPago = async () => {
            try {
                const params = new URLSearchParams(location.search);
                const token = params.get('token_ws');

                if (!token) {
                    throw new Error('Token no encontrado');
                }

                const response = await api.post('/transbank/confirmar', { token_ws: token });
                setResultado(response.data);

                if (response.data.status === 'AUTHORIZED') {
                    enqueueSnackbar('Pago realizado exitosamente', { variant: 'success' });
                } else {
                    enqueueSnackbar('El pago no pudo ser procesado', { variant: 'error' });
                }
            } catch (error) {
                console.error('Error al confirmar pago:', error);
                setError('Error al confirmar el pago');
                enqueueSnackbar('Error al confirmar el pago', { variant: 'error' });
            } finally {
                setLoading(false);
            }
        };

        confirmarPago();
    }, [location, enqueueSnackbar]);

    // Redirección automática si el pago fue exitoso
    useEffect(() => {
        if (resultado?.status === 'AUTHORIZED') {
            const timer = setTimeout(() => {
                navigate('/');
            }, 8000);
            return () => clearTimeout(timer);
        }
    }, [resultado, navigate]);

    const handleVolver = () => {
        navigate('/');
    };

    if (loading) {
        return (
            <Box
                sx={{
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    minHeight: '100vh'
                }}
            >
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Box
            sx={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                minHeight: '100vh',
                p: 2
            }}
        >
            <Card sx={{ maxWidth: 600, width: '100%', textAlign: 'center', py: 4 }}>
                <CardContent>
                    {error ? (
                        <>
                            <ErrorIcon sx={{ fontSize: 70, color: 'error.main', mb: 2 }} />
                            <Typography variant="h5" gutterBottom color="error">
                                ¡Error en el Pago!
                            </Typography>
                            <Alert severity="error" sx={{ mb: 2 }}>
                                {error}
                            </Alert>
                        </>
                    ) : (
                        <>
                            {resultado?.status === 'AUTHORIZED' ? (
                                <>
                                    <CheckCircleIcon sx={{ fontSize: 70, color: 'success.main', mb: 2 }} />
                                    <Typography variant="h5" gutterBottom color="success.main">
                                        ¡Pago Exitoso!
                                    </Typography>
                                    <Alert severity="success" sx={{ mb: 2 }}>
                                        El pago ha sido procesado exitosamente. Serás redirigido al inicio en unos segundos.
                                    </Alert>
                                </>
                            ) : (
                                <>
                                    <ErrorIcon sx={{ fontSize: 70, color: 'warning.main', mb: 2 }} />
                                    <Typography variant="h5" gutterBottom color="warning.main">
                                        Pago No Autorizado
                                    </Typography>
                                    <Alert severity="warning" sx={{ mb: 2 }}>
                                        El pago no pudo ser procesado. Por favor, intenta nuevamente o contacta soporte.
                                    </Alert>
                                </>
                            )}
                            <Box sx={{ mb: 2 }}>
                                <Typography variant="subtitle1" gutterBottom>
                                    Estado: {resultado?.status}
                                </Typography>
                                <Typography variant="body1" gutterBottom>
                                    Orden de Compra: {resultado?.buyOrder}
                                </Typography>
                                <Typography variant="body1" gutterBottom>
                                    Monto: ${resultado?.amount?.toLocaleString('es-CL')}
                                </Typography>
                                <Typography variant="body1" gutterBottom>
                                    Fecha: {resultado?.transactionDate ? new Date(resultado?.transactionDate).toLocaleString() : '-'}
                                </Typography>
                            </Box>
                        </>
                    )}
                    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={handleVolver}
                        >
                            Volver al Inicio
                        </Button>
                    </Box>
                </CardContent>
            </Card>
        </Box>
    );
};

export default ConfirmacionPago;
