import { useContext } from 'react';
import { ContextoAuth } from '../contexto/ContextoAuth';

export const useAuth = () => {
  const context = useContext(ContextoAuth);
  if (!context) {
    throw new Error('useAuth debe ser usado dentro de ProveedorAuth');
  }
  return context;
};
