import api from './api';

export const servicioPagos = {
  // Crear preferencia de pago
  crearPreferencia: async (items, comprador) => {
    try {
      const response = await api.post('/pagos/crear-preferencia', {
        items,
        comprador
      });
      return response.data;
    } catch (error) {
      console.error('Error al crear preferencia de pago:', error);
      throw error;
    }
  },

  // Procesar pago exitoso
  procesarPagoExitoso: async (paymentId, preferenceId) => {
    try {
      const response = await api.post('/pagos/procesar-exitoso', {
        paymentId,
        preferenceId
      });
      return response.data;
    } catch (error) {
      console.error('Error al procesar pago exitoso:', error);
      throw error;
    }
  },

  // Procesar pago fallido
  procesarPagoFallido: async (paymentId, preferenceId, error) => {
    try {
      const response = await api.post('/pagos/procesar-fallido', {
        paymentId,
        preferenceId,
        error
      });
      return response.data;
    } catch (error) {
      console.error('Error al procesar pago fallido:', error);
      throw error;
    }
  }
};
