// ==========================================
// ARCHIVO: frontend/src/paginas/PaginaPrincipal.jsx
// ==========================================
import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  ShoppingCartIcon, 
  HeartIcon,
  StarIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  ChevronDownIcon,
  TagIcon,
  TruckIcon,
  ShieldCheckIcon
} from '@heroicons/react/24/outline';
import { HeartIcon as HeartSolid, StarIcon as StarSolid } from '@heroicons/react/24/solid';
import { useCarrito } from '../contexto/ContextoCarrito';
import { useAuth } from '../contexto/ContextoAuth';
import { servicioProductos } from '../servicios/servicioProductos';
import CatalogoProductos from '../componentes/productos/CatalogoProductos';
import ListaMarcas from '../componentes/productos/ListaMarcas';

console.log('PAGINA PRINCIPAL SE MONTA');

const PaginaPrincipal = () => {
  const { agregarAlCarrito, carrito } = useCarrito();
  const { usuario } = useAuth();
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('todos');
  const [marcaSeleccionada, setMarcaSeleccionada] = useState('todas');
  const [busqueda, setBusqueda] = useState('');
  const [favoritos, setFavoritos] = useState([]);
  const [mostrarFiltros, setMostrarFiltros] = useState(false);
  const [ordenamiento, setOrdenamiento] = useState('destacados');

  // ESTADOS REALES
  const [productos, setProductos] = useState([]);
  const [categorias, setCategorias] = useState([{ id: 'todos', nombre: 'Todos los Productos', icono: 'ðŸ”§' }]);
  const [marcas, setMarcas] = useState([{ id: 'todas', nombre: 'Todas' }]);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    const cargarDatos = async () => {
      setCargando(true);
      try {
        const productosData = await servicioProductos.obtenerTodos();
        const marcasData = await servicioProductos.obtenerMarcas();
        const categoriasData = await servicioProductos.obtenerCategorias();
        setProductos(productosData.data?.productos || []);
        setMarcas([{ id: 'todas', nombre: 'Todas' }, ...(marcasData.data || [])]);
        setCategorias([{ id: 'todos', nombre: 'Todos los Productos', icono: 'ðŸ”§' }, ...(categoriasData.data || [])]);
      } catch (error) {
        console.error('Error al cargar datos:', error);
      } finally {
        setCargando(false);
      }
    };
    cargarDatos();
  }, []);

  // Filtrar productos (solo catÃ¡logo general)
  const productosFiltrados = productos.filter(producto => {
    const cumpleBusqueda = producto.nombre.toLowerCase().includes(busqueda.toLowerCase()) ||
      (producto.marca?.nombre?.toLowerCase().includes(busqueda.toLowerCase()));
    const cumpleCategoria = categoriaSeleccionada === 'todos' || producto.categoria?.id === categoriaSeleccionada;
    const cumpleMarca = marcaSeleccionada === 'todas' || producto.marca?.id === marcaSeleccionada;
    return cumpleBusqueda && cumpleCategoria && cumpleMarca;
  });

  // Ordenar productos
  const productosOrdenados = [...productosFiltrados].sort((a, b) => {
    switch (ordenamiento) {
      case 'precio-asc':
        return a.precio - b.precio;
      case 'precio-desc':
        return b.precio - a.precio;
      case 'nombre':
        return a.nombre.localeCompare(b.nombre);
      default:
        return 0;
    }
  });

  const toggleFavorito = (productoId) => {
    setFavoritos(prev => 
      prev.includes(productoId) 
        ? prev.filter(id => id !== productoId)
        : [...prev, productoId]
    );
  };

  const formatearPrecio = (precio) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP'
    }).format(precio);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section
        className="relative text-white py-16"
        style={{
          backgroundImage: "url('/assets/imagenes/banner/bannerferremas.png')",
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        {/* Overlay para oscurecer la imagen y mejorar la legibilidad */}
        <div className="absolute inset-0 bg-black opacity-40"></div>
        <div className="container mx-auto px-4 relative z-10">
          <div className="text-center">
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h1 className="text-5xl font-bold mb-4">
                FERREMAS
              </h1>
              <p className="text-xl mb-8 text-blue-100">
                Tu distribuidora de confianza desde 1980
              </p>
              <div className="flex flex-wrap justify-center gap-6 text-sm">
                <div className="flex items-center">
                  <TruckIcon className="h-5 w-5 mr-2" />
                  EnvÃ­o a todo Chile
                </div>
                <div className="flex items-center">
                  <ShieldCheckIcon className="h-5 w-5 mr-2" />
                  GarantÃ­a extendida
                </div>
                <div className="flex items-center">
                  <TagIcon className="h-5 w-5 mr-2" />
                  Mejores precios
                </div>
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Barra de bÃºsqueda y filtros */}
      <section className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex flex-col lg:flex-row gap-4 items-center">
            {/* Buscador */}
            <div className="relative flex-1 max-w-md">
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="text"
                placeholder="Buscar productos, marcas..."
                value={busqueda}
                onChange={(e) => setBusqueda(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>

            {/* Filtros */}
            <div className="flex gap-4 items-center">
              <button
                onClick={() => setMostrarFiltros(!mostrarFiltros)}
                className="flex items-center px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
              >
                <FunnelIcon className="h-5 w-5 mr-2" />
                Filtros
                <ChevronDownIcon className={`h-4 w-4 ml-2 transform transition-transform ${mostrarFiltros ? 'rotate-180' : ''}`} />
              </button>

              <select
                value={ordenamiento}
                onChange={(e) => setOrdenamiento(e.target.value)}
