// ==========================================
// ARCHIVO: frontend/src/App.jsx
// ==========================================
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

// Contextos - NOMBRES CORREGIDOS
import { AuthProvider } from './contexto/ContextoAuth';
import { CarritoProvider } from './contexto/ContextoCarrito';
import { TemaProvider } from './contexto/ContextoTema';
import { NotificacionProvider } from './contexto/ContextoNotificacion';

// Componentes de Layout
import Encabezado from './componentes/comun/Encabezado';
import BarraLateral from './componentes/comun/BarraLateral';
import PiePagina from './componentes/comun/PiePagina';
import Cargando from './componentes/comun/Cargando';

// Páginas de Autenticación
import IniciarSesion from './componentes/autenticacion/IniciarSesion';
import Registrarse from './componentes/autenticacion/Registrarse';

// Páginas Principales
import PaginaTablero from './paginas/PaginaTablero';
import PaginaClientes from './paginas/PaginaClientes';
import PaginaProveedores from './paginas/PaginaProveedores';
import PaginaProductos from './paginas/PaginaProductos';
import PaginaInventario from './paginas/PaginaInventario';
import PaginaPedidos from './paginas/PaginaPedidos';
import PaginaFacturas from './paginas/PaginaFacturas';
import PaginaPagos from './paginas/PaginaPagos';
import PaginaDescuentos from './paginas/PaginaDescuentos';
import PaginaIntegraciones from './paginas/PaginaIntegraciones';

// Hook personalizado para autenticación
import { useAuth } from './contexto/ContextoAuth';

// Componente para rutas protegidas
const RutaProtegida = ({ children }) => {
  const { usuario, cargando } = useAuth();
  
  if (cargando) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Cargando tamaño="lg" mensaje="Verificando autenticación..." />
      </div>
    );
  }
  
  return usuario ? children : <Navigate to="/iniciar-sesion" replace />;
};

// Componente para páginas de autenticación
const PaginaAuth = ({ children }) => {
  const { usuario } = useAuth();
  
  if (usuario) {
    return <Navigate to="/tablero" replace />;
  }
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
      <motion.div
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.3 }}
        className="w-full max-w-md"
      >
        {children}
      </motion.div>
    </div>
  );
};

// Componente principal de layout
const LayoutPrincipal = ({ children }) => {
  const [sidebarAbierto, setSidebarAbierto] = useState(false);
  
  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex">
      {/* Barra Lateral */}
      <BarraLateral 
        abierto={sidebarAbierto} 
        onCerrar={() => setSidebarAbierto(false)} 
      />
      
      {/* Contenido Principal */}
      <div className="flex-1 flex flex-col overflow-hidden lg:ml-64">
        {/* Encabezado */}
        <Encabezado onMenuClick={() => setSidebarAbierto(true)} />
        
        {/* Área de Contenido */}
        <main className="flex-1 overflow-y-auto">
          <AnimatePresence mode="wait">
            <motion.div
              key={window.location.pathname}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="p-6"
            >
              {children}
            </motion.div>
          </AnimatePresence>
        </main>
        
        {/* Pie de Página */}
        <PiePagina />
      </div>
      
      {/* Overlay para móvil */}
      {sidebarAbierto && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={() => setSidebarAbierto(false)}
        />
      )}
    </div>
  );
};

// Componente principal de la aplicación
function App() {
  const [cargandoInicial, setCargandoInicial] = useState(true);

  useEffect(() => {
    // Simular carga inicial de la aplicación
    const timer = setTimeout(() => {
      setCargandoInicial(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  if (cargandoInicial) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-600 to-purple-700 flex items-center justify-center">
        <motion.div
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
          className="text-center"
        >
          <motion.div
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
            className="w-16 h-16 bg-white rounded-2xl flex items-center justify-center mx-auto mb-4"
          >
            <span className="text-blue-600 font-bold text-2xl">F</span>
          </motion.div>
          <h1 className="text-white text-2xl font-bold mb-2">FERREMAS</h1>
          <p className="text-blue-100">Cargando aplicación...</p>
        </motion.div>
      </div>
    );
  }

  return (
    <TemaProvider>
      <AuthProvider>
        <CarritoProvider>
          <NotificacionProvider>
            <Router>
              <div className="App">
                <Routes>
                  {/* Rutas de Autenticación */}
                  <Route 
                    path="/iniciar-sesion" 
                    element={
                      <PaginaAuth>
                        <IniciarSesion />
                      </PaginaAuth>
                    } 
                  />
                  <Route 
                    path="/registrarse" 
                    element={
                      <PaginaAuth>
                        <Registrarse />
                      </PaginaAuth>
                    } 
                  />
                  
                  {/* Rutas Protegidas */}
                  <Route path="/" element={<Navigate to="/tablero" replace />} />
                  <Route 
                    path="/tablero" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaTablero />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/clientes/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaClientes />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/proveedores/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaProveedores />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/productos/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaProductos />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/inventario/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaInventario />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/pedidos/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaPedidos />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/facturas/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaFacturas />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/pagos/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaPagos />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/descuentos/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaDescuentos />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  <Route 
                    path="/integraciones/*" 
                    element={
                      <RutaProtegida>
                        <LayoutPrincipal>
                          <PaginaIntegraciones />
                        </LayoutPrincipal>
                      </RutaProtegida>
                    } 
                  />
                  
                  {/* Ruta por defecto */}
                  <Route path="*" element={<Navigate to="/tablero" replace />} />
                </Routes>
              </div>
            </Router>
          </NotificacionProvider>
        </CarritoProvider>
      </AuthProvider>
    </TemaProvider>
  );
}

export default App;
