// ==========================================
// ARCHIVO: src/componentes/TestConexion.jsx - PRUEBA DIRECTA
// ==========================================
import React, { useState } from 'react';

const TestConexion = () => {
  const [resultados, setResultados] = useState(null);
  const [cargando, setCargando] = useState(false);

  const probarConexion = async () => {
    setCargando(true);
    setResultados(null);

    try {
      console.log('🔥 PROBANDO CONEXIÓN...');
      
      // Probar productos
      const respuestaProductos = await fetch('http://localhost:3001/api/productos');
      const datosProductos = await respuestaProductos.json();
      
      // Probar marcas
      const respuestaMarcas = await fetch('http://localhost:3001/api/productos/marcas');
      const datosMarcas = await respuestaMarcas.json();
      
      // Probar categorías
      const respuestaCategorias = await fetch('http://localhost:3001/api/productos/categorias');
      const datosCategorias = await respuestaCategorias.json();

      setResultados({
        productos: datosProductos,
        marcas: datosMarcas,
        categorias: datosCategorias
      });

      console.log('✅ RESULTADOS:', {
        productos: datosProductos,
        marcas: datosMarcas,
        categorias: datosCategorias
      });

    } catch (error) {
      console.error('❌ ERROR:', error);
      setResultados({ error: error.message });
    } finally {
      setCargando(false);
    }
  };

  return (
    <div className="bg-white p-8 rounded-lg shadow-lg max-w-4xl mx-auto my-8">
      <h2 className="text-2xl font-bold mb-4">🔥 TEST DE CONEXIÓN DIRECTA</h2>
      
      <button
        onClick={probarConexion}
        disabled={cargando}
        className={`px-6 py-3 rounded-lg text-white font-medium ${
          cargando 
            ? 'bg-gray-400 cursor-not-allowed' 
            : 'bg-red-600 hover:bg-red-700'
        }`}
      >
        {cargando ? '🔄 Probando...' : '🚀 PROBAR CONEXIÓN AHORA'}
      </button>

      {resultados && (
        <div className="mt-6 space-y-4">
          {resultados.error ? (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4">
              <h3 className="text-red-800 font-bold">❌ ERROR:</h3>
              <p className="text-red-600">{resultados.error}</p>
            </div>
          ) : (
            <>
              {/* Productos */}
              <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                <h3 className="text-green-800 font-bold">✅ PRODUCTOS:</h3>
                <p>Total: {resultados.productos.data?.length || 0}</p>
                <div className="max-h-40 overflow-y-auto">
                  <pre className="text-xs">{JSON.stringify(resultados.productos, null, 2)}</pre>
                </div>
              </div>

              {/* Marcas */}
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <h3 className="text-blue-800 font-bold">✅ MARCAS:</h3>
                <p>Total: {resultados.marcas.data?.length || 0}</p>
                <div className="max-h-40 overflow-y-auto">
                  <pre className="text-xs">{JSON.stringify(resultados.marcas, null, 2)}</pre>
                </div>
              </div>

              {/* Categorías */}
              <div className="bg-purple-50 border border-purple-200 rounded-lg p-4">
                <h3 className="text-purple-800 font-bold">✅ CATEGORÍAS:</h3>
                <p>Total: {resultados.categorias.data?.length || 0}</p>
                <div className="max-h-40 overflow-y-auto">
                  <pre className="text-xs">{JSON.stringify(resultados.categorias, null, 2)}</pre>
                </div>
              </div>

              {/* Productos para mostrar */}
              {resultados.productos.data && resultados.productos.data.length > 0 && (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                  <h3 className="text-yellow-800 font-bold">🎯 PRODUCTOS PARA MOSTRAR:</h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
                    {resultados.productos.data.slice(0, 6).map(producto => (
                      <div key={producto.id} className="bg-white border rounded p-3">
                        <h4 className="font-bold text-sm">{producto.nombre}</h4>
                        <p className="text-xs text-gray-600">{producto.marca_nombre}</p>
                        <p className="text-lg font-bold text-red-600">
                          ${new Intl.NumberFormat('es-CL').format(producto.precio)}
                        </p>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default TestConexion;
