import React, { useState } from 'react';
import { registrarIngresoStock, registrarEgresoStock, registrarAjusteStock } from '../../servicios/servicioInventario';

const MovimientoStock = ({ productoId }) => {
  const [tipo, setTipo] = useState('ingreso');
  const [cantidad, setCantidad] = useState(0);
  const [motivo, setMotivo] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [cargando, setCargando] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMensaje('');
    setCargando(true);
    try {
      const data = { productoId, cantidad: Number(cantidad), motivo };
      if (tipo === 'ingreso') await registrarIngresoStock(data);
      if (tipo === 'egreso') await registrarEgresoStock(data);
      if (tipo === 'ajuste') await registrarAjusteStock(data);
      setMensaje('✅ Movimiento registrado correctamente');
      setCantidad(0);
      setMotivo('');
    } catch (err) {
      setMensaje('❌ Error: ' + (err.response?.data?.error || 'No se pudo registrar el movimiento'));
    } finally {
      setCargando(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem', maxWidth: 350 }}>
      <label>
        Tipo de movimiento:
        <select value={tipo} onChange={e => setTipo(e.target.value)}>
          <option value="ingreso">Ingreso</option>
          <option value="egreso">Egreso</option>
          <option value="ajuste">Ajuste</option>
        </select>
      </label>
      <label>
        Cantidad:
        <input type="number" value={cantidad} onChange={e => setCantidad(e.target.value)} min="0" required />
      </label>
      <label>
        Motivo:
        <input type="text" value={motivo} onChange={e => setMotivo(e.target.value)} placeholder="Motivo" required />
      </label>
      <button type="submit" disabled={cargando}>{cargando ? 'Procesando...' : 'Registrar movimiento'}</button>
      {mensaje && <div style={{ marginTop: 8 }}>{mensaje}</div>}
    </form>
  );
};

export default MovimientoStock;
