import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import {
  UsersIcon,
  CubeIcon,
  ShoppingBagIcon,
  CurrencyDollarIcon,
  TrendingUpIcon,
  TrendingDownIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  ClockIcon,
  EyeIcon,
} from '@heroicons/react/24/outline';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { useQuery } from 'react-query';
import { servicioReportes } from '../servicios/servicioReportes';
import TarjetaEstadistica from '../componentes/tablero/TarjetaEstadistica';
import Cargando from '../componentes/comun/Cargando';

// Registrar componentes de Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const PaginaTablero = () => {
  const [periodoSeleccionado, setPeriodoSeleccionado] = useState('mes');

  // Datos de ejemplo para el dashboard
  const estadisticasEjemplo = {
    ventasMes: 2450000,
    cambioVentas: 12.5,
    pedidosActivos: 45,
    cambioPedidos: -5.2,
    productosStock: 1250,
    cambioStock: 8.3,
    clientesActivos: 89,
    cambioClientes: 15.7,
  };

  // Datos de ejemplo para los gráficos
  const datosVentas = {
    labels: ['Ene', 'Feb', 'Mar', 'Abr', 'May', 'Jun', 'Jul'],
    datasets: [
      {
        label: 'Ventas',
        data: [1200000, 1900000, 1500000, 2100000, 1800000, 2200000, 2450000],
        borderColor: 'rgb(59, 130, 246)',
        backgroundColor: 'rgba(59, 130, 246, 0.1)',
        fill: true,
        tension: 0.4,
      },
    ],
  };

  const opcionesVentas = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Evolución de Ventas',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          callback: function(value) {
            return '$' + value.toLocaleString();
          },
        },
      },
    },
  };

  const datosProductos = {
    labels: ['Herramientas', 'Materiales', 'Eléctricos', 'Plomería', 'Pintura'],
    datasets: [
      {
        data: [30, 25, 20, 15, 10],
        backgroundColor: [
          '#3B82F6',
          '#10B981',
          '#F59E0B',
          '#EF4444',
          '#8B5CF6',
        ],
        borderWidth: 0,
      },
    ],
  };

  const opcionesProductos = {
    responsive: true,
    plugins: {
      legend: {
        position: 'bottom',
      },
      title: {
        display: true,
        text: 'Ventas por Categoría',
      },
    },
  };

  // Datos de ejemplo para tarjetas estadísticas
  const tarjetasEstadisticas = [
    {
      titulo: 'Ventas del Mes',
      valor: estadisticasEjemplo.ventasMes,
      formato: 'moneda',
      cambio: estadisticasEjemplo.cambioVentas,
      icono: CurrencyDollarIcon,
      color: 'blue',
      descripcion: 'vs mes anterior',
    },
    {
      titulo: 'Pedidos Activos',
      valor: estadisticasEjemplo.pedidosActivos,
      formato: 'numero',
      cambio: estadisticasEjemplo.cambioPedidos,
      icono: ShoppingBagIcon,
      color: 'green',
      descripcion: 'pendientes de entrega',
    },
    {
      titulo: 'Productos en Stock',
      valor: estadisticasEjemplo.productosStock,
      formato: 'numero',
      cambio: estadisticasEjemplo.cambioStock,
      icono: CubeIcon,
      color: 'purple',
      descripcion: 'productos disponibles',
    },
    {
      titulo: 'Clientes Activos',
      valor: estadisticasEjemplo.clientesActivos,
      formato: 'numero',
      cambio: estadisticasEjemplo.cambioClientes,
      icono: UsersIcon,
      color: 'orange',
      descripcion: 'este mes',
    },
  ];

  // Alertas de ejemplo
  const alertasStock = [
    { id: 1, producto: 'Taladro Bosch GBH 2-20', stock: 2, minimo: 5 },
    { id: 2, producto: 'Cemento Portland 25kg', stock: 8, minimo: 20 },
    { id: 3, producto: 'Cable eléctrico 2.5mm', stock: 15, minimo: 50 },
  ];

  const pedidosRecientes = [
    { id: 'PED-001', cliente: 'Constructora ABC', monto: 450000, estado: 'pendiente' },
    { id: 'PED-002', cliente: 'Ferretería El Martillo', monto: 280000, estado: 'aprobado' },
    { id: 'PED-003', cliente: 'Juan Pérez', monto: 125000, estado: 'preparando' },
    { id: 'PED-004', cliente: 'Edificios SA', monto: 890000, estado: 'enviado' },
  ];

  const obtenerColorEstado = (estado) => {
    const colores = {
      pendiente: 'bg-yellow-100 text-yellow-800',
      aprobado: 'bg-blue-100 text-blue-800',
      preparando: 'bg-orange-100 text-orange-800',
      enviado: 'bg-purple-100 text-purple-800',
      entregado: 'bg-green-100 text-green-800',
      cancelado: 'bg-red-100 text-red-800',
    };
    return colores[estado] || 'bg-gray-100 text-gray-800';
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="space-y-6"
    >
      {/* Encabezado */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            Tablero Principal
          </h1>
          <p className="text-gray-600 dark:text-gray-400 mt-1">
            Resumen ejecutivo de tu negocio en tiempo real
          </p>
        </div>
        <div className="mt-4 md:mt-0">
          <select
            value={periodoSeleccionado}
            onChange={(e) => setPeriodoSeleccionado(e.target.value)}
            className="form-input"
          >
            <option value="dia">Hoy</option>
            <option value="semana">Esta Semana</option>
            <option value="mes">Este Mes</option>
            <option value="año">Este Año</option>
          </select>
        </div>
      </div>

      {/* Tarjetas de Estadísticas */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {tarjetasEstadisticas.map((tarjeta, index) => (
          <TarjetaEstadistica key={index} {...tarjeta} />
        ))}
      </div>

      {/* Gráficos */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Gráfico de Ventas */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.2 }}
          className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 p-6"
        >
          <Line data={datosVentas} options={opcionesVentas} />
        </motion.div>

        {/* Gráfico de Productos por Categoría */}
        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.3 }}
          className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 p-6"
        >
          <Doughnut data={datosProductos} options={opcionesProductos} />
        </motion.div>
      </div>

      {/* Sección de Alertas y Actividades */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Alertas de Stock */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 p-6"
        >
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
              Alertas de Stock
            </h3>
            <ExclamationTriangleIcon className="w-5 h-5 text-yellow-500" />
          </div>
          <div className="space-y-3">
            {alertasStock.map((alerta) => (
              <div
                key={alerta.id}
                className="flex items-center justify-between p-3 bg-yellow-50 dark:bg-yellow-900/20 rounded-lg border border-yellow-200 dark:border-yellow-700"
              >
                <div>
                  <p className="text-sm font-medium text-gray-900 dark:text-white">
                    {alerta.producto}
                  </p>
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    Stock: {alerta.stock} / Mínimo: {alerta.minimo}
                  </p>
                </div>
                <span className="px-2 py-1 text-xs font-medium bg-yellow-200 text-yellow-800 rounded-full">
                  Bajo
                </span>
              </div>
            ))}
          </div>
        </motion.div>

        {/* Pedidos Recientes */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
          className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 p-6"
        >
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
              Pedidos Recientes
            </h3>
            <ShoppingBagIcon className="w-5 h-5 text-blue-500" />
          </div>
          <div className="space-y-3">
            {pedidosRecientes.map((pedido) => (
              <div
                key={pedido.id}
                className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-700 rounded-lg"
              >
                <div>
                  <p className="text-sm font-medium text-gray-900 dark:text-white">
                    {pedido.id}
                  </p>
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    {pedido.cliente}
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-sm font-semibold text-gray-900 dark:text-white">
                    ${pedido.monto.toLocaleString()}
                  </p>
                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${obtenerColorEstado(pedido.estado)}`}>
                    {pedido.estado}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </motion.div>
      </div>

      {/* Acciones Rápidas */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.6 }}
        className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 p-6"
      >
        <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
          Acciones Rápidas
        </h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <button className="flex flex-col items-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg hover:bg-blue-100 dark:hover:bg-blue-900/30 transition-colors">
            <ShoppingBagIcon className="w-8 h-8 text-blue-600 dark:text-blue-400 mb-2" />
            <span className="text-sm font-medium text-blue-900 dark:text-blue-200">
              Nuevo Pedido
            </span>
          </button>
          <button className="flex flex-col items-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg hover:bg-green-100 dark:hover:bg-green-900/30 transition-colors">
            <CubeIcon className="w-8 h-8 text-green-600 dark:text-green-400 mb-2" />
            <span className="text-sm font-medium text-green-900 dark:text-green-200">
              Añadir Producto
            </span>
          </button>
          <button className="flex flex-col items-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg hover:bg-purple-100 dark:hover:bg-purple-900/30 transition-colors">
            <UsersIcon className="w-8 h-8 text-purple-600 dark:text-purple-400 mb-2" />
            <span className="text-sm font-medium text-purple-900 dark:text-purple-200">
              Nuevo Cliente
            </span>
          </button>
          <button className="flex flex-col items-center p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg hover:bg-orange-100 dark:hover:bg-orange-900/30 transition-colors">
            <EyeIcon className="w-8 h-8 text-orange-600 dark:text-orange-400 mb-2" />
            <span className="text-sm font-medium text-orange-900 dark:text-orange-200">
              Ver Reportes
            </span>
          </button>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default PaginaTablero;
