import { useContext } from 'react';
import { ContextoTema } from '../contexto/ContextoTema';

export const useTema = () => {
  const contexto = useContext(ContextoTema);
  if (!contexto) {
    throw new Error('useTema debe usarse dentro de ProveedorTema');
  }
  return contexto;
};
