import React, { useState } from 'react';
import MovimientoStock from './MovimientoStock';

// Simulación de inventario para el ejemplo
const inventarioDemo = [
  {
    id: 1,
    producto: { id: 1, nombre: 'Taladro Eléctrico DeWalt 20V' },
    stock_actual: 25,
    stock_minimo: 5,
    stock_maximo: 50
  },
  {
    id: 2,
    producto: { id: 2, nombre: 'Sierra Circular Bosch 7.25"' },
    stock_actual: 3,
    stock_minimo: 5,
    stock_maximo: 30
  }
];

const ListaInventario = () => {
  const [formAbierto, setFormAbierto] = useState(null);

  return (
    <div>
      <h2>Inventario</h2>
      <table border="1" cellPadding="8" style={{ width: '100%', marginBottom: 24 }}>
        <thead>
          <tr>
            <th>Producto</th>
            <th>Stock actual</th>
            <th>Mínimo</th>
            <th>Máximo</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {inventarioDemo.map(item => (
            <React.Fragment key={item.id}>
              <tr>
                <td>{item.producto.nombre}</td>
                <td>{item.stock_actual}</td>
                <td>{item.stock_minimo}</td>
                <td>{item.stock_maximo}</td>
                <td>
                  <button onClick={() => setFormAbierto(formAbierto === item.id ? null : item.id)}>
                    {formAbierto === item.id ? 'Cerrar' : '+ Movimiento'}
                  </button>
                </td>
              </tr>
              {formAbierto === item.id && (
                <tr>
                  <td colSpan={5} style={{ background: '#f8fafc' }}>
                    <MovimientoStock productoId={item.producto.id} modoCompacto />
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListaInventario;
