import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  MapPinIcon, 
  CreditCardIcon,
  TruckIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  ArrowLeftIcon
} from '@heroicons/react/24/outline';
import { useCarrito } from '../contexto/ContextoCarrito';
import { useAuth } from '../contexto/ContextoAuth';
import { useNotificacion } from '../contexto/ContextoNotificacion';
import { servicioPagos } from '../servicios/servicioPagos';

const ConfirmacionPago = () => {
  const navigate = useNavigate();
  const { carrito, obtenerTotal, limpiarCarrito } = useCarrito();
  const { usuario } = useAuth();
  const { mostrarNotificacion } = useNotificacion();
  
  const [formData, setFormData] = useState({
    nombre: usuario?.nombre || '',
    email: usuario?.email || '',
    telefono: '',
    direccion: '',
    comuna: '',
    ciudad: 'Santiago',
    codigoPostal: '',
    instrucciones: ''
  });
  
  const [cargando, setCargando] = useState(false);
  const [error, setError] = useState(null);
  const [mapaCargado, setMapaCargado] = useState(false);

  // Cargar Google Maps
  useEffect(() => {
    const cargarGoogleMaps = () => {
      if (window.google && window.google.maps) {
        setMapaCargado(true);
        return;
      }

      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = () => setMapaCargado(true);
      script.onerror = () => setError('Error al cargar Google Maps');
      document.head.appendChild(script);
    };

    cargarGoogleMaps();
  }, []);

  // Inicializar mapa cuando se carga Google Maps
  useEffect(() => {
    if (mapaCargado && formData.direccion) {
      inicializarMapa();
    }
  }, [mapaCargado, formData.direccion]);

  // Manejar confirmación de pago desde Webpay
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('token_ws');
    
    if (token) {
      handleConfirmacionPago(token);
    }
  }, []);

  const inicializarMapa = () => {
    const geocoder = new window.google.maps.Geocoder();
    const direccionCompleta = `${formData.direccion}, ${formData.comuna}, ${formData.ciudad}, Chile`;
    
    geocoder.geocode({ address: direccionCompleta }, (results, status) => {
      if (status === 'OK') {
        const map = new window.google.maps.Map(document.getElementById('mapa'), {
          center: results[0].geometry.location,
          zoom: 15
        });
        
        new window.google.maps.Marker({
          map: map,
          position: results[0].geometry.location,
          title: 'Dirección de entrega'
        });
      }
    });
  };

  // Manejar confirmación de pago desde Webpay
  const handleConfirmacionPago = async (token) => {
    setCargando(true);
    setError(null);

    try {
      const response = await servicioPagos.confirmarTransaccionTransbank(token);

      if (response.success) {
        mostrarNotificacion('¡Pago confirmado exitosamente!', 'success');
        limpiarCarrito();
        navigate('/pago-exitoso', { 
          state: { 
            ordenCompra: response.data.orden_compra,
            monto: response.data.monto,
            fecha: response.data.fecha_transaccion
          } 
        });
      } else {
        setError('El pago fue rechazado');
        mostrarNotificacion('El pago fue rechazado', 'error');
      }
    } catch (error) {
      console.error('Error al confirmar pago:', error);
      setError('Error al confirmar el pago');
      mostrarNotificacion('Error al confirmar el pago', 'error');
    } finally {
      setCargando(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const formatearPrecio = (precio) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
      minimumFractionDigits: 0
    }).format(precio);
  };

  const calcularSubtotal = () => obtenerTotal();
  const calcularIVA = () => calcularSubtotal() * 0.19;
  const calcularTotal = () => calcularSubtotal() + calcularIVA();

  const handlePagarConWebpay = async () => {
    if (!formData.direccion || !formData.comuna || !formData.telefono) {
      mostrarNotificacion('Por favor completa todos los campos obligatorios', 'error');
      return;
    }

    setCargando(true);
    setError(null);

    try {
      // Preparar datos para Transbank
      const datosTransaccion = {
        usuario_id: usuario?.id,
        productos: carrito.map(item => ({
          producto_id: item.id,
          cantidad: item.cantidad,
          precio_unitario: item.precio
        })),
        direccion_entrega: {
          nombre: formData.nombre,
          telefono: formData.telefono,
          direccion: formData.direccion,
          comuna: formData.comuna,
          ciudad: formData.ciudad,
          codigo_postal: formData.codigoPostal,
          instrucciones: formData.instrucciones
        },
        subtotal: calcularSubtotal(),
        iva: calcularIVA(),
        total: calcularTotal()
      };

      // Crear transacción en Transbank
      const response = await servicioPagos.crearTransaccionTransbank(datosTransaccion);

      if (response.success) {
        // Redirigir a Webpay
        window.location.href = response.data.url;
      } else {
        throw new Error(response.message || 'Error al procesar el pago');
      }

    } catch (error) {
      console.error('Error al procesar pago:', error);
      setError(error.message || 'Error al procesar el pago');
      mostrarNotificacion('Error al procesar el pago', 'error');
    } finally {
      setCargando(false);
    }
  };

  if (carrito.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <CheckCircleIcon className="h-16 w-16 text-green-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Carrito vacío</h2>
          <p className="text-gray-600 mb-4">No hay productos en tu carrito para procesar el pago.</p>
          <button
            onClick={() => navigate('/')}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
          >
            Volver al inicio
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <button
              onClick={() => navigate(-1)}
              className="flex items-center text-gray-600 hover:text-gray-900 transition-colors"
            >
              <ArrowLeftIcon className="h-5 w-5 mr-2" />
              Volver
            </button>
            <h1 className="text-2xl font-bold text-gray-900">Confirmar Pedido</h1>
            <div></div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Formulario de entrega */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="bg-white rounded-lg shadow-sm p-6"
          >
            <h2 className="text-xl font-bold text-gray-900 mb-6 flex items-center">
              <MapPinIcon className="h-6 w-6 mr-2 text-blue-600" />
              Información de Entrega
            </h2>

            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Nombre completo *
                  </label>
                  <input
                    type="text"
                    name="nombre"
                    value={formData.nombre}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Teléfono *
                  </label>
                  <input
                    type="tel"
                    name="telefono"
                    value={formData.telefono}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    required
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Dirección *
                </label>
                <input
                  type="text"
                  name="direccion"
                  value={formData.direccion}
                  onChange={handleInputChange}
                  placeholder="Calle, número, departamento"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  required
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Comuna *
                  </label>
                  <input
                    type="text"
                    name="comuna"
                    value={formData.comuna}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Ciudad
                  </label>
                  <input
                    type="text"
                    name="ciudad"
                    value={formData.ciudad}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Instrucciones de entrega
                </label>
                <textarea
                  name="instrucciones"
                  value={formData.instrucciones}
                  onChange={handleInputChange}
                  rows={3}
                  placeholder="Instrucciones adicionales para la entrega..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>
          </motion.div>

          {/* Resumen del pedido */}
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            className="space-y-6"
          >
            {/* Resumen */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h2 className="text-xl font-bold text-gray-900 mb-4 flex items-center">
                <TruckIcon className="h-6 w-6 mr-2 text-green-600" />
                Resumen del Pedido
              </h2>

              <div className="space-y-3">
                {carrito.map((item) => (
                  <div key={item.id} className="flex justify-between items-center py-2 border-b border-gray-100">
                    <div className="flex-1">
                      <h4 className="font-medium text-gray-900">{item.nombre}</h4>
                      <p className="text-sm text-gray-600">Cantidad: {item.cantidad}</p>
                    </div>
                    <div className="text-right">
                      <p className="font-medium text-gray-900">
                        {formatearPrecio(item.precio * item.cantidad)}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              <div className="mt-4 pt-4 border-t border-gray-200">
                <div className="flex justify-between mb-2">
                  <span className="text-gray-600">Subtotal:</span>
                  <span className="font-medium">{formatearPrecio(calcularSubtotal())}</span>
                </div>
                <div className="flex justify-between mb-2">
                  <span className="text-gray-600">IVA (19%):</span>
                  <span className="font-medium">{formatearPrecio(calcularIVA())}</span>
                </div>
                <div className="flex justify-between text-lg font-bold">
                  <span>Total:</span>
                  <span className="text-red-600">{formatearPrecio(calcularTotal())}</span>
                </div>
              </div>
            </div>

            {/* Mapa */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Ubicación de Entrega</h3>
              <div 
                id="mapa" 
                className="w-full h-64 bg-gray-200 rounded-lg"
                style={{ minHeight: '256px' }}
              >
                {!mapaCargado && (
                  <div className="flex items-center justify-center h-full">
                    <p className="text-gray-500">Cargando mapa...</p>
                  </div>
                )}
                {error && (
                  <div className="flex items-center justify-center h-full">
                    <p className="text-red-500">{error}</p>
                  </div>
                )}
              </div>
            </div>

            {/* Botón de pago */}
            <div className="bg-white rounded-lg shadow-sm p-6">
              <button
                onClick={handlePagarConWebpay}
                disabled={cargando || !formData.direccion || !formData.comuna || !formData.telefono}
                className="w-full bg-blue-600 text-white py-3 px-6 rounded-lg hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors flex items-center justify-center"
              >
                {cargando ? (
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                ) : (
                  <>
                    <CreditCardIcon className="h-5 w-5 mr-2" />
                    Pagar con Webpay
                  </>
                )}
              </button>
              
              {error && (
                <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded-lg">
                  <div className="flex items-center">
                    <ExclamationTriangleIcon className="h-5 w-5 text-red-500 mr-2" />
                    <p className="text-red-700 text-sm">{error}</p>
                  </div>
                </div>
              )}
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default ConfirmacionPago;
