import React, { useEffect, useState } from 'react';
import { servicioProductos } from '../servicios/servicioProductos';
import { ShoppingCartIcon } from '@heroicons/react/24/outline';
import { Link } from 'react-router-dom';

const Ofertas = () => {
  const [productos, setProductos] = useState([]);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    const cargar = async () => {
      setCargando(true);
      try {
        const res = await servicioProductos.obtenerTodos();
        setProductos(res.data?.productos || []);
      } catch (e) {
        setProductos([]);
      } finally {
        setCargando(false);
      }
    };
    cargar();
  }, []);

  // Filtrar solo productos en oferta
  const productosEnOferta = productos.filter(
    p => p.precioAnterior || (p.etiquetas && p.etiquetas.includes('Promoción'))
  );

  // Agrupar por categoría
  const productosPorCategoria = productosEnOferta.reduce((acc, prod) => {
    const cat = prod.categoria?.nombre || 'Otros';
    if (!acc[cat]) acc[cat] = [];
    acc[cat].push(prod);
    return acc;
  }, {});

  const formatearPrecio = (precio) => {
    return new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP',
    }).format(precio);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-12">
        <h1 className="text-4xl font-bold text-center text-red-600 mb-10">Ofertas Especiales</h1>
        {cargando ? (
          <div className="text-center py-20 text-gray-500">Cargando ofertas...</div>
        ) : productosEnOferta.length === 0 ? (
          <div className="text-center py-20 text-gray-500">No hay productos en oferta actualmente.</div>
        ) : (
          Object.entries(productosPorCategoria).map(([categoria, productos]) => (
            <div key={categoria} className="mb-12">
              <h2 className="text-2xl font-bold text-gray-900 mb-6">{categoria}</h2>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {productos.map(producto => (
                  <div key={producto.id} className="bg-white rounded-lg shadow-sm overflow-hidden group hover:shadow-lg transition-all duration-300">
                    <div className="relative">
                      <img
                        src={producto.imagen ? `/assets/imagenes/productos/${producto.imagen}` : '/assets/imagenes/productos/Sierrabosch.jpg'}
                        alt={producto.nombre}
                        className="w-full h-48 object-contain bg-white"
                      />
                      <div className="absolute top-2 left-2 bg-red-500 text-white px-2 py-1 rounded text-xs font-semibold">OFERTA</div>
                    </div>
                    <div className="p-4">
                      <span className="text-sm text-blue-600 font-medium">{producto.marca?.nombre}</span>
                      <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2">{producto.nombre}</h3>
                      <div className="mb-4">
                        <div className="flex items-center space-x-2">
                          <span className="text-xl font-bold text-gray-900">{formatearPrecio(producto.precio)}</span>
                          {producto.precioAnterior && (
                            <span className="text-sm text-gray-500 line-through">{formatearPrecio(producto.precioAnterior)}</span>
                          )}
                        </div>
                        {producto.precioAnterior && (
                          <span className="text-sm text-green-600 font-medium">Ahorra {formatearPrecio(producto.precioAnterior - producto.precio)}</span>
                        )}
                      </div>
                      <button
                        className="w-full flex items-center justify-center px-4 py-2 rounded-md font-medium transition-colors bg-red-600 text-white hover:bg-red-700"
                      >
                        <ShoppingCartIcon className="h-5 w-5 mr-2" />
                        Agregar al carrito
                      </button>
                      <Link
                        to={`/producto/${producto.id}`}
                        className="w-full flex items-center justify-center px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 transition-colors mt-2"
                      >
                        Ver detalles
                      </Link>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Ofertas;
