// ==========================================
// ARCHIVO: frontend/src/componentes/comun/BarraLateral.jsx
// ==========================================
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import {
  HomeIcon,
  UsersIcon,
  TruckIcon,
  CubeIcon,
  ArchiveBoxIcon,
  ShoppingCartIcon,
  DocumentTextIcon,
  CreditCardIcon,
  TicketIcon,
  Cog6ToothIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';

const menuItems = [
  { 
    id: 'tablero', 
    nombre: 'Tablero', 
    ruta: '/tablero', 
    icono: HomeIcon,
    descripcion: 'Panel principal'
  },
  { 
    id: 'clientes', 
    nombre: 'Clientes', 
    ruta: '/clientes', 
    icono: UsersIcon,
    descripcion: 'Gestión de clientes'
  },
  { 
    id: 'proveedores', 
    nombre: 'Proveedores', 
    ruta: '/proveedores', 
    icono: TruckIcon,
    descripcion: 'Gestión de proveedores'
  },
  { 
    id: 'productos', 
    nombre: 'Productos', 
    ruta: '/productos', 
    icono: CubeIcon,
    descripcion: 'Catálogo de productos'
  },
  { 
    id: 'inventario', 
    nombre: 'Inventario', 
    ruta: '/inventario', 
    icono: ArchiveBoxIcon,
    descripcion: 'Control de stock'
  },
  { 
    id: 'pedidos', 
    nombre: 'Pedidos', 
    ruta: '/pedidos', 
    icono: ShoppingCartIcon,
    descripcion: 'Gestión de pedidos'
  },
  { 
    id: 'facturas', 
    nombre: 'Facturas', 
    ruta: '/facturas', 
    icono: DocumentTextIcon,
    descripcion: 'Emisión de facturas'
  },
  { 
    id: 'pagos', 
    nombre: 'Pagos', 
    ruta: '/pagos', 
    icono: CreditCardIcon,
    descripcion: 'Gestión de pagos'
  },
  { 
    id: 'descuentos', 
    nombre: 'Descuentos', 
    ruta: '/descuentos', 
    icono: TicketIcon,
    descripcion: 'Promociones y descuentos'
  },
  { 
    id: 'integraciones', 
    nombre: 'Integraciones', 
    ruta: '/integraciones', 
    icono: Cog6ToothIcon,
    descripcion: 'Integraciones externas'
  }
];

const BarraLateral = ({ abierto, onCerrar }) => {
  const location = useLocation();

  const esRutaActiva = (ruta) => {
    return location.pathname.startsWith(ruta);
  };

  return (
    <>
      {/* Sidebar Desktop */}
      <div className="hidden lg:flex lg:w-64 lg:flex-col lg:fixed lg:inset-y-0">
        <div className="flex-1 flex flex-col min-h-0 bg-white dark:bg-gray-800 border-r border-gray-200 dark:border-gray-700">
          <div className="flex-1 flex flex-col pt-5 pb-4 overflow-y-auto">
            <div className="flex items-center flex-shrink-0 px-4">
              <motion.div
                whileHover={{ scale: 1.05 }}
                className="w-8 h-8 bg-gradient-to-br from-blue-500 to-blue-600 rounded-lg flex items-center justify-center"
              >
                <span className="text-white font-bold text-lg">F</span>
              </motion.div>
              <span className="ml-3 text-xl font-bold text-gray-900 dark:text-white">
                FERREMAS
              </span>
            </div>
            <nav className="mt-8 flex-1 px-2 space-y-1">
              {menuItems.map((item) => {
                const Icono = item.icono;
                const activo = esRutaActiva(item.ruta);
                
                return (
                  <Link
                    key={item.id}
                    to={item.ruta}
                    className={`group flex items-center px-3 py-2.5 text-sm font-medium rounded-lg transition-all duration-200 ${
                      activo
                        ? 'bg-blue-50 dark:bg-blue-900/20 text-blue-700 dark:text-blue-300 border-r-2 border-blue-500'
                        : 'text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 hover:text-gray-900 dark:hover:text-white'
                    }`}
                  >
                    <Icono className={`mr-3 flex-shrink-0 h-5 w-5 ${
                      activo ? 'text-blue-500' : 'text-gray-400 group-hover:text-gray-500'
                    }`} />
                    {item.nombre}
                  </Link>
                );
              })}
            </nav>
          </div>
        </div>
      </div>

      {/* Sidebar Mobile */}
      <AnimatePresence>
        {abierto && (
          <motion.div
            initial={{ x: -300 }}
            animate={{ x: 0 }}
            exit={{ x: -300 }}
            transition={{ type: "spring", stiffness: 300, damping: 30 }}
            className="lg:hidden fixed inset-y-0 left-0 z-50 w-64 bg-white dark:bg-gray-800 shadow-xl"
          >
            <div className="flex-1 flex flex-col min-h-0">
              <div className="flex items-center justify-between flex-shrink-0 px-4 py-4 border-b border-gray-200 dark:border-gray-700">
                <div className="flex items-center">
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    className="w-8 h-8 bg-gradient-to-br from-blue-500 to-blue-600 rounded-lg flex items-center justify-center"
                  >
                    <span className="text-white font-bold text-lg">F</span>
                  </motion.div>
                  <span className="ml-3 text-xl font-bold text-gray-900 dark:text-white">
                    FERREMAS
                  </span>
                </div>
                <button
                  onClick={onCerrar}
                  className="p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700"
                >
                  <XMarkIcon className="w-5 h-5" />
                </button>
              </div>
              
              <nav className="flex-1 px-2 py-4 space-y-1 overflow-y-auto">
                {menuItems.map((item) => {
                  const Icono = item.icono;
                  const activo = esRutaActiva(item.ruta);
                  
                  return (
                    <Link
                      key={item.id}
                      to={item.ruta}
                      onClick={onCerrar}
                      className={`group flex items-center px-3 py-2.5 text-sm font-medium rounded-lg transition-all duration-200 ${
                        activo
                          ? 'bg-blue-50 dark:bg-blue-900/20 text-blue-700 dark:text-blue-300'
                          : 'text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 hover:text-gray-900 dark:hover:text-white'
                      }`}
                    >
                      <Icono className={`mr-3 flex-shrink-0 h-5 w-5 ${
                        activo ? 'text-blue-500' : 'text-gray-400 group-hover:text-gray-500'
                      }`} />
                      <div>
                        <div>{item.nombre}</div>
                        <div className="text-xs text-gray-500 dark:text-gray-400">
                          {item.descripcion}
                        </div>
                      </div>
                    </Link>
                  );
                })}
              </nav>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
};

export default BarraLateral;
