// ==========================================
// ARCHIVO: frontend/src/componentes/comun/Encabezado.jsx
// ==========================================
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Bars3Icon, 
  BellIcon, 
  MagnifyingGlassIcon,
  SunIcon,
  MoonIcon,
  UserCircleIcon,
  CogIcon,
  ArrowRightOnRectangleIcon
} from '@heroicons/react/24/outline';
import { useAuth } from '../../contexto/ContextoAuth';
import { useTema } from '../../contexto/ContextoTema';
import { useCarrito } from '../../contexto/ContextoCarrito';

const Encabezado = ({ onMenuClick }) => {
  const { usuario, cerrarSesion } = useAuth();
  const { modoOscuro, alternarTema } = useTema();
  const { cantidadProductos } = useCarrito();
  const [menuUsuarioAbierto, setMenuUsuarioAbierto] = useState(false);
  const [busqueda, setBusqueda] = useState('');

  const manejarCerrarSesion = () => {
    cerrarSesion();
    setMenuUsuarioAbierto(false);
  };

  return (
    <header className="bg-white dark:bg-gray-800 shadow-lg border-b border-gray-200 dark:border-gray-700 sticky top-0 z-50">
      <div className="flex items-center justify-between h-16 px-4 sm:px-6 lg:px-8">
        {/* Lado Izquierdo */}
        <div className="flex items-center space-x-4">
          {/* Botón Menú Móvil */}
          <button
            onClick={onMenuClick}
            className="lg:hidden p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700"
          >
            <Bars3Icon className="w-6 h-6" />
          </button>

          {/* Logo y Título */}
          <div className="flex items-center space-x-3">
            <motion.div
              whileHover={{ scale: 1.05 }}
              className="w-8 h-8 bg-gradient-to-br from-blue-500 to-blue-600 rounded-lg flex items-center justify-center"
            >
              <span className="text-white font-bold text-lg">F</span>
            </motion.div>
            <h1 className="hidden sm:block text-xl font-bold text-gray-900 dark:text-white">
              FERREMAS
            </h1>
          </div>
        </div>

        {/* Centro - Barra de Búsqueda */}
        <div className="hidden md:flex flex-1 max-w-md mx-8">
          <div className="relative w-full">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <MagnifyingGlassIcon className="w-5 h-5 text-gray-400" />
            </div>
            <input
              type="text"
              value={busqueda}
              onChange={(e) => setBusqueda(e.target.value)}
              placeholder="Buscar productos, clientes..."
              className="block w-full pl-10 pr-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-700 text-gray-900 dark:text-white placeholder-gray-500 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
        </div>

        {/* Lado Derecho */}
        <div className="flex items-center space-x-4">
          {/* Botón Tema */}
          <motion.button
            whileTap={{ scale: 0.95 }}
            onClick={alternarTema}
            className="p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700"
          >
            {modoOscuro ? (
              <SunIcon className="w-5 h-5" />
            ) : (
              <MoonIcon className="w-5 h-5" />
            )}
          </motion.button>

          {/* Notificaciones */}
          <div className="relative">
            <button className="p-2 rounded-lg text-gray-500 hover:text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:text-gray-200 dark:hover:bg-gray-700">
              <BellIcon className="w-5 h-5" />
              {cantidadProductos > 0 && (
                <span className="absolute top-0 right-0 w-4 h-4 bg-red-500 text-white text-xs rounded-full flex items-center justify-center">
                  {cantidadProductos > 9 ? '9+' : cantidadProductos}
                </span>
              )}
            </button>
          </div>

          {/* Usuario */}
          <div className="relative">
            <button
              onClick={() => setMenuUsuarioAbierto(!menuUsuarioAbierto)}
              className="flex items-center space-x-2 p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700"
            >
              <UserCircleIcon className="w-8 h-8 text-gray-400" />
              <span className="hidden sm:block text-sm font-medium text-gray-700 dark:text-gray-300">
                {usuario?.nombre}
              </span>
            </button>

            {/* Dropdown Menu Usuario */}
            <AnimatePresence>
              {menuUsuarioAbierto && (
                <motion.div
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: 10 }}
                  className="absolute right-0 mt-2 w-64 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-50"
                >
                  <div className="p-4 border-b border-gray-200 dark:border-gray-700">
                    <p className="text-sm font-medium text-gray-900 dark:text-white">
                      {usuario?.nombre}
                    </p>
                    <p className="text-sm text-gray-500 dark:text-gray-400">
                      {usuario?.email}
                    </p>
                    <p className="text-xs text-blue-600 dark:text-blue-400 mt-1">
                      {usuario?.rol}
                    </p>
                  </div>
                  
                  <div className="py-2">
                    <button className="flex items-center space-x-3 w-full px-4 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700">
                      <CogIcon className="w-4 h-4" />
                      <span>Configuración</span>
                    </button>
                    
                    <button
                      onClick={manejarCerrarSesion}
                      className="flex items-center space-x-3 w-full px-4 py-2 text-sm text-red-600 dark:text-red-400 hover:bg-gray-100 dark:hover:bg-gray-700"
                    >
                      <ArrowRightOnRectangleIcon className="w-4 h-4" />
                      <span>Cerrar Sesión</span>
                    </button>
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Encabezado;
