import { useContext } from 'react';
import { ContextoNotificacion } from '../contexto/ContextoNotificacion';

export const useNotificacion = () => {
  const context = useContext(ContextoNotificacion);
  if (!context) {
    throw new Error('useNotificacion debe ser usado dentro de ProveedorNotificacion');
  }
  return context;
};
